import React, { useEffect, useRef } from 'react';

const PlayerProfile = () => {
  const abilityFillsRef = useRef([]);

  useEffect(() => {
    // Animate ability bars when component mounts
    const observer = new IntersectionObserver((entries) => {
      entries.forEach(entry => {
        if (entry.isIntersecting) {
          const fill = entry.target;
          const width = fill.style.width;
          fill.style.width = '0%';
          setTimeout(() => {
            fill.style.width = width;
          }, 200);
          observer.unobserve(entry.target);
        }
      });
    }, { threshold: 0.5 });

    abilityFillsRef.current.forEach(fill => {
      if (fill) observer.observe(fill);
    });

    return () => observer.disconnect();
  }, []);

  const abilities = [
    { name: 'Unity Development', level: 95 },
    { name: 'AR/VR Technology', level: 90 },
    { name: 'Multiplayer Systems', level: 88 },
    { name: 'Game Design', level: 85 }
  ];

  return (
    <section id="about" className="game-section character-sheet">
      <div className="container">
        <h2 className="section-title">Player Profile</h2>
        <div className="character-stats">
          <div className="stat-block">
            <div className="character-description">
              <p>
                Dedicated Unity Developer with 2+ years of experience in game development and AR/VR applications. 
                Skilled in Unity, C#, AR, VR, and multiplayer plugins like Photon PUN. I have successfully developed 
                a range of hyper-casual and hybrid-casual games, showcasing my ability to blend technical excellence 
                with creative design. Passionate about delivering immersive user experiences and staying ahead in the 
                evolving field of digital interaction.
              </p>
            </div>
            <div className="ability-bars">
              {abilities.map((ability, index) => (
                <div key={ability.name} className="ability">
                  <span>{ability.name}</span>
                  <div className="ability-bar">
                    <div 
                      className="ability-fill"
                      ref={el => abilityFillsRef.current[index] = el}
                      style={{ width: `${ability.level}%` }}
                    ></div>
                  </div>
                </div>
              ))}
            </div>
          </div>
        </div>
      </div>
    </section>
  );
};

export default PlayerProfile;
