import React, { useState, useEffect, useRef } from 'react';

const ArcadeGame = () => {
  const [score, setScore] = useState(0);
  const [highScore, setHighScore] = useState(0);
  const [gameActive, setGameActive] = useState(false);
  const [status, setStatus] = useState('Click Start Game to begin!');

  const canvasRef = useRef(null);
  const gameLoopRef = useRef(null);
  const keysRef = useRef({});

  const playerRef = useRef({ x: 200, y: 250, width: 20, height: 20 });
  const collectiblesRef = useRef([]);
  const gameSpeedRef = useRef(2);

  useEffect(() => {
    // Load high score from localStorage
    const savedHighScore = localStorage.getItem('arcadeHighScore');
    if (savedHighScore) {
      setHighScore(parseInt(savedHighScore));
    }

    // Keyboard event listeners
    const handleKeyDown = (e) => {
      keysRef.current[e.key.toLowerCase()] = true;
    };

    const handleKeyUp = (e) => {
      keysRef.current[e.key.toLowerCase()] = false;
    };

    document.addEventListener('keydown', handleKeyDown);
    document.addEventListener('keyup', handleKeyUp);

    return () => {
      document.removeEventListener('keydown', handleKeyDown);
      document.removeEventListener('keyup', handleKeyUp);
      if (gameLoopRef.current) {
        cancelAnimationFrame(gameLoopRef.current);
      }
    };
  }, []);

  const startGame = () => {
    setGameActive(true);
    setScore(0);
    setStatus('Use arrow keys or WASD to move!');

    playerRef.current = { x: 200, y: 250, width: 20, height: 20 };
    collectiblesRef.current = [];
    gameSpeedRef.current = 2;

    gameLoop();
  };

  const gameLoop = () => {
    if (!gameActive) return;

    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');

    // Clear canvas
    ctx.fillStyle = '#1a1a1a';
    ctx.fillRect(0, 0, canvas.width, canvas.height);

    // Handle player movement
    const keys = keysRef.current;
    if (keys['arrowleft'] || keys['a']) {
      playerRef.current.x = Math.max(0, playerRef.current.x - 5);
    }
    if (keys['arrowright'] || keys['d']) {
      playerRef.current.x = Math.min(canvas.width - playerRef.current.width, playerRef.current.x + 5);
    }
    if (keys['arrowup'] || keys['w']) {
      playerRef.current.y = Math.max(0, playerRef.current.y - 5);
    }
    if (keys['arrowdown'] || keys['s']) {
      playerRef.current.y = Math.min(canvas.height - playerRef.current.height, playerRef.current.y + 5);
    }

    // Draw player
    ctx.fillStyle = '#fbbf24';
    ctx.fillRect(playerRef.current.x, playerRef.current.y, playerRef.current.width, playerRef.current.height);

    // Spawn collectibles
    if (Math.random() < 0.02) {
      collectiblesRef.current.push({
        x: Math.random() * (canvas.width - 10),
        y: -10,
        width: 10,
        height: 10,
        speed: gameSpeedRef.current
      });
    }

    // Update and draw collectibles
    ctx.fillStyle = '#3b82f6';
    for (let i = collectiblesRef.current.length - 1; i >= 0; i--) {
      const collectible = collectiblesRef.current[i];
      collectible.y += collectible.speed;

      ctx.fillRect(collectible.x, collectible.y, collectible.width, collectible.height);

      // Check collision
      if (playerRef.current.x < collectible.x + collectible.width &&
          playerRef.current.x + playerRef.current.width > collectible.x &&
          playerRef.current.y < collectible.y + collectible.height &&
          playerRef.current.y + playerRef.current.height > collectible.y) {

        collectiblesRef.current.splice(i, 1);
        setScore(prevScore => {
          const newScore = prevScore + 10;
          if (newScore > highScore) {
            setHighScore(newScore);
            try {
              localStorage.setItem('arcadeHighScore', newScore.toString());
            } catch (e) {
              // Ignore localStorage errors
            }
          }
          return newScore;
        });

        // Increase speed slightly
        gameSpeedRef.current += 0.01;
      }

      // Remove off-screen collectibles
      if (collectible.y > canvas.height) {
        collectiblesRef.current.splice(i, 1);
      }
    }

    gameLoopRef.current = requestAnimationFrame(gameLoop);
  };

  const stopGame = () => {
    setGameActive(false);
    setStatus(`Game Over! Final Score: ${score}. Click Start Game to try again!`);
    if (gameLoopRef.current) {
      cancelAnimationFrame(gameLoopRef.current);
    }
  };

  return (
    <section className="mini-game-section">
      <div className="container">
        <div className="mini-game-container">
          <h3 className="mini-game-title">Retro Arcade Challenge</h3>
          <p className="mini-game-desc">Classic arcade action! Collect the golden pixels!</p>
          <div className="arcade-game">
            <div className="game-stats">
              <span>Score: <span className="score-display">{score}</span></span>
              <span>High Score: <span className="score-display">{highScore}</span></span>
              <button 
                className="btn-game" 
                onClick={gameActive ? stopGame : startGame}
              >
                {gameActive ? 'Stop Game' : 'Start Game'}
              </button>
            </div>
            <canvas 
              ref={canvasRef}
              className="arcade-canvas" 
              width="400" 
              height="300"
            ></canvas>
            <div className="game-status">{status}</div>
          </div>
        </div>
      </div>
    </section>
  );
};

export default ArcadeGame;
