import React, { useEffect, useState } from 'react';
import './App.css';
import Sidebar from './components/Sidebar';
import PlayerProfile from './components/PlayerProfile';
import QuestLog from './components/QuestLog';
import CareerCampaign from './components/CareerCampaign';
import TrainingAcademy from './components/TrainingAcademy';
import SkillTree from './components/SkillTree';
import DeveloperLogs from './components/DeveloperLogs';
import GuildChannels from './components/GuildChannels';
import ParticleBackground from './components/ParticleBackground';

function App() {
  const [activeSection, setActiveSection] = useState('about');

  useEffect(() => {
    // Update active section based on scroll position
    const handleScroll = () => {
      const sections = ['about', 'projects', 'experience', 'skills', 'blog', 'contact'];
      const scrollPosition = window.scrollY + 150;

      for (const sectionId of sections) {
        const element = document.getElementById(sectionId);
        if (element) {
          const sectionTop = element.offsetTop;
          const sectionHeight = element.offsetHeight;

          if (scrollPosition >= sectionTop && scrollPosition < sectionTop + sectionHeight) {
            setActiveSection(sectionId);
            break;
          }
        }
      }
    };

    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  return (
    <div className="App gaming-theme" data-color-scheme="dark">
      <ParticleBackground />

      <div className="portfolio-layout">
        <Sidebar activeSection={activeSection} setActiveSection={setActiveSection} />

        <main className="main-content">
          <PlayerProfile />
          <QuestLog />
          <CareerCampaign />
          <TrainingAcademy />
          <SkillTree />
          <DeveloperLogs />
          <GuildChannels />
        </main>
      </div>
    </div>
  );
}

export default App;
