import React, { useState, useEffect, useRef } from 'react';

const BugSquasherGame = () => {
  const [score, setScore] = useState(0);
  const [gameActive, setGameActive] = useState(true);
  const bugFieldRef = useRef(null);
  const gameIntervalRef = useRef(null);

  const createScorePopup = (x, y, points) => {
    const popup = document.createElement('div');
    popup.textContent = `+${points}`;
    popup.style.cssText = `
      position: fixed;
      left: ${x}px;
      top: ${y}px;
      color: #ffd700;
      font-weight: bold;
      font-size: 16px;
      pointer-events: none;
      z-index: 1000;
      animation: scorePopup 1s ease-out forwards;
    `;

    document.body.appendChild(popup);

    setTimeout(() => {
      if (popup.parentNode) {
        popup.remove();
      }
    }, 1000);
  };

  const handleBugClick = (e, bugElement) => {
    e.stopPropagation();
    e.preventDefault();

    // Prevent multiple clicks on the same bug
    if (bugElement.dataset.squashed === 'true') {
      return;
    }

    // Mark bug as squashed
    bugElement.dataset.squashed = 'true';

    console.log('Bug clicked! Current score:', score);

    // Update score using functional update
    setScore(prevScore => {
      const newScore = prevScore + 10;
      console.log('Score updated from', prevScore, 'to', newScore);
      return newScore;
    });

    // Squash effect
    bugElement.textContent = 'üí•';
    bugElement.style.animation = 'none';
    bugElement.style.transform = 'scale(1.5)';
    bugElement.style.color = '#ffd700';
    bugElement.style.cursor = 'default';

    // Create score popup
    const rect = bugFieldRef.current.getBoundingClientRect();
    createScorePopup(rect.left + parseInt(bugElement.style.left) + 40, rect.top + parseInt(bugElement.style.top), 10);

    // Remove bug after explosion effect
    setTimeout(() => {
      if (bugElement.parentNode) {
        bugElement.remove();
      }
    }, 300);
  };

  const createBug = () => {
    if (!gameActive || !bugFieldRef.current) return;

    const bug = document.createElement('div');
    bug.className = 'bug';
    bug.textContent = 'üêõ';
    bug.dataset.squashed = 'false';

    // Slower animation - 4-6 seconds instead of 2-4
    const animationDuration = Math.random() * 2 + 4;

    bug.style.cssText = `
      position: absolute;
      top: ${Math.random() * (bugFieldRef.current.offsetHeight - 40)}px;
      left: -40px;
      font-size: 28px;
      cursor: pointer;
      user-select: none;
      z-index: 10;
      animation: bugMove ${animationDuration}s linear forwards;
      pointer-events: auto;
      transition: transform 0.2s ease;
    `;

    // Add click event listener
    bug.addEventListener('click', (e) => handleBugClick(e, bug));

    // Add hover effect
    bug.addEventListener('mouseenter', () => {
      if (bug.dataset.squashed !== 'true') {
        bug.style.transform = 'scale(1.3)';
      }
    });

    bug.addEventListener('mouseleave', () => {
      if (bug.dataset.squashed !== 'true') {
        bug.style.transform = 'scale(1)';
      }
    });

    bugFieldRef.current.appendChild(bug);

    // Remove bug if not clicked within animation time
    setTimeout(() => {
      if (bug.parentNode && bug.dataset.squashed !== 'true') {
        bug.remove();
      }
    }, animationDuration * 1000);
  };

  const resetGame = () => {
    console.log('Resetting game...');
    setScore(0);
    setGameActive(true);

    // Clear existing bugs
    if (bugFieldRef.current) {
      const bugs = bugFieldRef.current.querySelectorAll('.bug');
      bugs.forEach(bug => bug.remove());
    }
  };

  useEffect(() => {
    // Add CSS for animations
    if (!document.querySelector('#bug-game-styles')) {
      const style = document.createElement('style');
      style.id = 'bug-game-styles';
      style.textContent = `
        @keyframes bugMove {
          from { left: -40px; }
          to { left: 100%; }
        }
        @keyframes scorePopup {
          0% { 
            transform: translateY(0) scale(1);
            opacity: 1;
          }
          100% { 
            transform: translateY(-50px) scale(1.2);
            opacity: 0;
          }
        }
      `;
      document.head.appendChild(style);
    }

    // Start bug spawning - slower spawn rate (every 1.5 seconds instead of 1)
    gameIntervalRef.current = setInterval(createBug, 1500);

    return () => {
      if (gameIntervalRef.current) {
        clearInterval(gameIntervalRef.current);
      }
    };
  }, [gameActive]);

  // Debug effect to log score changes
  useEffect(() => {
    console.log('Score changed to:', score);
  }, [score]);

  return (
    <section className="mini-game-section">
      <div className="container">
        <div className="mini-game-container">
          <h3 className="mini-game-title">Debug Bug Squasher</h3>
          <p className="mini-game-desc">Click to squash those pesky bugs!</p>
          <div className="bug-game">
            <div className="game-stats">
              <span>Score: <span className="score-display">{score}</span></span>
              <button className="btn-game" onClick={resetGame}>Reset</button>
            </div>
            <div ref={bugFieldRef} className="bug-field">
              <div className="bug-spawn-area">
                Click here to squash bugs! üêõ
              </div>
            </div>
          </div>
        </div>
      </div>
    </section>
  );
};

export default BugSquasherGame;
