import React from 'react';

const Navigation = ({ activeSection, setActiveSection }) => {
  const handleNavClick = (e, sectionId) => {
    e.preventDefault();

    const targetSection = document.getElementById(sectionId);
    if (targetSection) {
      // Visual feedback
      e.target.style.boxShadow = '0 0 20px rgba(251, 191, 36, 0.8)';
      setTimeout(() => {
        e.target.style.boxShadow = '';
      }, 300);

      // Smooth scroll
      const headerOffset = 100;
      const elementPosition = targetSection.getBoundingClientRect().top + window.pageYOffset;
      const offsetPosition = elementPosition - headerOffset;

      window.scrollTo({
        top: offsetPosition,
        behavior: 'smooth'
      });

      setActiveSection(sectionId);
    }
  };

  const menuItems = [
    { id: 'about', label: 'Player Profile' },
    { id: 'projects', label: 'Quest Log' },
    { id: 'experience', label: 'Career Campaign' },
    { id: 'mini-games', label: 'Mini-Games' },
    { id: 'blog', label: 'Developer Logs' },
    { id: 'contact', label: 'Guild Channels' }
  ];

  return (
    <nav className="game-menu">
      <div className="container">
        <ul className="menu-items">
          {menuItems.map((item) => (
            <li key={item.id}>
              <a
                href={`#${item.id}`}
                className={`menu-link ${activeSection === item.id ? 'active' : ''}`}
                onClick={(e) => handleNavClick(e, item.id)}
              >
                {item.label}
              </a>
            </li>
          ))}
        </ul>
      </div>
    </nav>
  );
};

export default Navigation;
