import React from 'react';

const CareerCampaign = () => {
  const careerLevels = [
    {
      level: 4,
      company: 'Nexchar Ventures',
      role: 'Game Programmer',
      period: 'June 2025 – Present',
      xp: 1000,
      current: true,
      description: 'Currently working on building gamified and educational tools using Unity, C#, and real-time multiplayer systems. Focused on immersive and accessible designs tailored for underserved users.'
    },
    {
      level: 3,
      company: 'Saathi',
      role: 'Game Programmer',
      period: 'Oct 2023 – May 2025',
      xp: 800,
      current: false,
      description: 'Specialized in developing hyper-casual and hybrid-casual games, as well as AR and VR applications. Worked on rapid prototyping, technical design, and feature pipelines.'
    },
    {
      level: 2,
      company: 'Meta Extended Reality',
      role: 'Game/AR-VR Programmer',
      period: 'Mar 2023 – Sep 2023',
      xp: 600,
      current: false,
      description: 'Contributed to cutting-edge AR and XR production. Worked with teams to integrate advanced Unity functionality into scalable projects.'
    },
    {
      level: 1,
      company: 'Zeap Solutions',
      role: 'Game Programmer Intern',
      period: 'Dec 2022 – Mar 2023',
      xp: 300,
      current: false,
      description: 'Supported cross-platform development. Assisted in debugging, level design, and multiplayer features.'
    }
  ];

  return (
    <section id="experience" className="game-section career-campaign">
      <div className="container">
        <h2 className="section-title">Career Campaign</h2>
        <div className="campaign-path">
          {careerLevels.map((level) => (
            <div key={level.level} className={`level-node level-${level.level} ${level.current ? 'current' : ''}`}>
              <div className="level-info">
                <div className="level-number">{level.level}</div>
                <h3>{level.company}</h3>
                <p className="job-role">{level.role}</p>
                <p className="job-period">{level.period}</p>
                <div className="xp-gained">+{level.xp} XP</div>
              </div>
              <div className="level-details">
                <p>{level.description}</p>
              </div>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default CareerCampaign;
