import React from 'react';

const CareerCampaign = () => {
  const experiences = [
    {
      id: 1,
      company: "Nexchar Ventures",
      role: "Senior Game Developer",
      period: "2023 - Present",
      xp: "Unity, C#, AR/VR",
      details: "Leading development of AR/VR applications and multiplayer games. Mentoring junior developers and implementing best practices for performance optimization and code quality."
    },
    {
      id: 2,
      company: "Game Studio X",
      role: "Game Developer",
      period: "2021 - 2023",
      xp: "Unity, C#, Multiplayer",
      details: "Developed multiple hyper-casual and hybrid-casual games. Implemented AR features and multiplayer functionality using Photon PUN. Collaborated with design and art teams to deliver polished gaming experiences."
    },
    {
      id: 3,
      company: "Tech Innovations Inc",
      role: "Junior Developer",
      period: "2020 - 2021",
      xp: "Unity, C#, Mobile",
      details: "Started my journey in game development, working on mobile games and learning industry best practices. Contributed to various projects and gained hands-on experience with Unity and C#."
    }
  ];

  return (
    <section id="experience" className="game-section">
      <h2 className="section-title">Work Experience</h2>
      <div className="campaign-path">
        {experiences.map((exp, index) => (
          <div key={exp.id} className={`level-node ${index === 0 ? 'current' : ''}`}>
            <div className="level-number">{exp.id}</div>
            <div className="level-info">
              <h3>{exp.company}</h3>
              <div className="job-role">{exp.role}</div>
              <div className="job-period">{exp.period}</div>
              <div className="xp-gained">{exp.xp}</div>
              <div className="level-details">
                {exp.details}
              </div>
            </div>
          </div>
        ))}
      </div>
    </section>
  );
};

export default CareerCampaign;
