import React, { useState, useEffect, useRef } from 'react';

const BugSquasherGame = () => {
  const [score, setScore] = useState(0);
  const [gameActive, setGameActive] = useState(true);
  const bugFieldRef = useRef(null);
  const gameIntervalRef = useRef(null);

  const createScorePopup = (x, y, points) => {
    const popup = document.createElement('div');
    popup.textContent = `+${points}`;
    popup.style.cssText = `
      position: fixed;
      left: ${x}px;
      top: ${y}px;
      color: #ffd700;
      font-weight: bold;
      font-size: 16px;
      pointer-events: none;
      z-index: 1000;
      animation: scorePopup 1s ease-out forwards;
    `;

    document.body.appendChild(popup);

    setTimeout(() => {
      if (popup.parentNode) {
        popup.remove();
      }
    }, 1000);
  };

  const createBug = () => {
    if (!gameActive || !bugFieldRef.current) return;

    const bug = document.createElement('div');
    bug.className = 'bug';
    bug.textContent = '🐛';
    bug.style.cssText = `
      position: absolute;
      top: ${Math.random() * (bugFieldRef.current.offsetHeight - 40)}px;
      left: -40px;
      font-size: 24px;
      cursor: pointer;
      user-select: none;
      z-index: 10;
      animation: bugMove ${Math.random() * 2 + 2}s linear forwards;
      pointer-events: auto;
    `;

    const handleBugClick = function(e) {
      e.stopPropagation();
      e.preventDefault();

      console.log('Bug clicked!'); // Debug log
      setScore(prevScore => prevScore + 10);

      // Squash effect
      this.textContent = '💥';
      this.style.animation = 'none';
      this.style.transform = 'scale(1.5)';
      this.style.color = '#ffd700';

      // Create score popup
      const rect = bugFieldRef.current.getBoundingClientRect();
      createScorePopup(rect.left + parseInt(this.style.left) + 40, rect.top + parseInt(this.style.top), 10);

      // Remove click listener to prevent multiple clicks
      this.removeEventListener('click', handleBugClick);

      setTimeout(() => {
        if (this.parentNode) {
          this.remove();
        }
      }, 200);
    };

    bug.addEventListener('click', handleBugClick);

    bugFieldRef.current.appendChild(bug);

    setTimeout(() => {
      if (bug.parentNode) {
        bug.remove();
      }
    }, 3000);
  };

  const resetGame = () => {
    setScore(0);
    setGameActive(true);

    // Clear existing bugs
    if (bugFieldRef.current) {
      const bugs = bugFieldRef.current.querySelectorAll('.bug');
      bugs.forEach(bug => bug.remove());
    }
  };

  useEffect(() => {
    // Add CSS for animations
    if (!document.querySelector('#bug-game-styles')) {
      const style = document.createElement('style');
      style.id = 'bug-game-styles';
      style.textContent = `
        @keyframes bugMove {
          from { left: -40px; }
          to { left: 100%; }
        }
        @keyframes scorePopup {
          0% { 
            transform: translateY(0) scale(1);
            opacity: 1;
          }
          100% { 
            transform: translateY(-50px) scale(1.2);
            opacity: 0;
          }
        }
      `;
      document.head.appendChild(style);
    }

    // Start bug spawning
    gameIntervalRef.current = setInterval(createBug, 1000);

    return () => {
      if (gameIntervalRef.current) {
        clearInterval(gameIntervalRef.current);
      }
    };
  }, [gameActive]);

  return (
    <section className="mini-game-section">
      <div className="container">
        <div className="mini-game-container">
          <h3 className="mini-game-title">Debug Bug Squasher</h3>
          <p className="mini-game-desc">Click to squash those pesky bugs!</p>
          <div className="bug-game">
            <div className="game-stats">
              <span>Score: <span className="score-display">{score}</span></span>
              <button className="btn-game" onClick={resetGame}>Reset</button>
            </div>
            <div ref={bugFieldRef} className="bug-field">
              <div className="bug-spawn-area">
                Click here to squash bugs! 🐛
              </div>
            </div>
          </div>
        </div>
      </div>
    </section>
  );
};

export default BugSquasherGame;
