import React, { useEffect, useState } from 'react';
import './App.css';
import Header from './components/Header';
import Navigation from './components/Navigation';
import PlayerProfile from './components/PlayerProfile';
import BugSquasherGame from './components/games/BugSquasherGame';
import QuestLog from './components/QuestLog';
import MemoryGame from './components/games/MemoryGame';
import CareerCampaign from './components/CareerCampaign';
import TrainingAcademy from './components/TrainingAcademy';
import SkillTree from './components/SkillTree';
import ArcadeGame from './components/games/ArcadeGame';
import DeveloperLogs from './components/DeveloperLogs';
import GuildChannels from './components/GuildChannels';
import ParticleBackground from './components/ParticleBackground';

function App() {
  const [activeSection, setActiveSection] = useState('about');

  useEffect(() => {
    // Update active section based on scroll position
    const handleScroll = () => {
      const sections = ['about', 'projects', 'experience', 'mini-games', 'blog', 'contact'];
      const scrollPosition = window.scrollY + 150;

      for (const sectionId of sections) {
        const element = document.getElementById(sectionId);
        if (element) {
          const sectionTop = element.offsetTop;
          const sectionHeight = element.offsetHeight;

          if (scrollPosition >= sectionTop && scrollPosition < sectionTop + sectionHeight) {
            setActiveSection(sectionId);
            break;
          }
        }
      }
    };

    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  return (
    <div className="App gaming-theme" data-color-scheme="dark">
      <ParticleBackground />

      <Header />
      <Navigation activeSection={activeSection} setActiveSection={setActiveSection} />

      <main>
        <PlayerProfile />
        <BugSquasherGame />
        <QuestLog />
        <MemoryGame />
        <CareerCampaign />
        <TrainingAcademy />
        <SkillTree />
        <ArcadeGame />
        <DeveloperLogs />
        <GuildChannels />
      </main>
    </div>
  );
}

export default App;
